    def button_clicked(self, button_text):
        # --- FIX (Ù…Ø¹Ø§Ù„Ø¬Ø© Ø±Ù…Ø² Ø§Ù„Ù‚Ø³Ù…Ø© Ã·) ---
        if button_text.isdigit():
            self.handle_digit(button_text)
        elif button_text == '.':
            self.handle_decimal()
        elif button_text in ['/', 'Ã·', 'Ã—', '-', '+']:
            # Convert Ã· to / for internal processing
            if button_text == 'Ã·':
                button_text = '/'
            self.handle_operation(button_text)
        elif button_text == '=':
            self.handle_equals()
        elif button_text == 'C':
            self.handle_clear()
        elif button_text == 'âŒ«':
            self.handle_backspace()
        elif button_text == '%':
            self.handle_percent()
        elif button_text == 'Â±':
            self.handle_sign_change()

    def handle_digit(self, digit):
        if self.new_number:
            self.current_value = digit
            self.new_number = False
        else:
            if self.current_value == "0":
                self.current_value = digit
            else:
                self.current_value += digit
        self.update_display()

    def handle_decimal(self):
        if self.new_number:
            self.current_value = "0."
            self.new_number = False
        elif '.' not in self.current_value:
            self.current_value += '.'
        self.update_display()

    def handle_operation(self, op):
        if self.operation and not self.new_number:
            self.handle_equals()

        self.previous_value = self.current_value
        self.operation = op
        self.new_number = True

    def handle_equals(self):
        if not self.operation or not self.previous_value:
            return

        try:
            prev = float(self.previous_value)
            curr = float(self.current_value)

            if self.operation == '+':
                result = prev + curr
            elif self.operation == '-':
                result = prev - curr
            elif self.operation == 'Ã—':
                result = prev * curr
            elif self.operation == '/':
                if curr == 0:
                    self.current_value = "Ø®Ø·Ø£"
                    self.update_display()
                    self.reset()
                    return
                result = prev / curr

            if result == int(result):
                self.current_value = str(int(result))
            else:
                self.current_value = str(round(result, 8))

            self.update_display()
            self.previous_value = ""
            self.operation = ""
            self.new_number = True

        except Exception:
            self.current_value = "Ø®Ø·Ø£"
            self.update_display()
            self.reset()

    def handle_clear(self):
        self.current_value = "0"
        self.previous_value = ""
        self.operation = ""
        self.new_number = True
        self.update_display()

    def handle_backspace(self):
        if not self.new_number and len(self.current_value) > 1:
            self.current_value = self.current_value[:-1]
        else:
            self.current_value = "0"
            self.new_number = True
        self.update_display()

    def handle_percent(self):
        try:
            value = float(self.current_value)
            self.current_value = str(value / 100)
            self.update_display()
            self.new_number = True
        except Exception:
            pass

    def handle_sign_change(self):
        try:
            value = float(self.current_value)
            self.current_value = str(-value)
            self.update_display()
        except Exception:
            pass

    def reset(self):
        self.previous_value = ""
        self.operation = ""
        self.new_number = True

    def update_display(self):
        display_text = self.current_value

        if len(display_text) > 15:
            try:
                num = float(display_text)
                display_text = f"{num:.6e}"
            except:
                display_text = display_text[:15]

        self.display.setText(display_text)

